import React from 'react';
import './App.css';
//제작한 리엑트 컴포넌트
import UserRegister from './components/UserRegister';
import Users from './components/Users';

//마테리얼 ui 컴포넌트들
import {Paper, Table, TableHead, TableBody, TableRow, TableCell, Typography, AppBar, Toolbar, IconButton, Divider, Drawer,} from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Hidden from '@material-ui/core/Hidden';

import MenuIcon from '@material-ui/icons/Menu'
// import SearchIcon from '@material-ui/icons/Search'
import {withStyles} from '@material-ui/core/styles';
import { fade } from '@material-ui/core/styles/colorManipulator';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { style } from '@material-ui/system';

const drawerWidth = 240;

const useStyles = makeStyles(theme =>({
  root: {
    display: 'flex',
    fontSize: 10,
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  // appbar의 길이 조절
  appBar:{
    marginLeft: drawerWidth,
    [theme.breakpoints.up('sm')]:{
      width: `cal(100% - ${drawerWidth}px)`,
    }
  },
  menu:{
    paddingRight:0,
    // marginTop: 15,
    marginBottom : 15,
    display: 'flax',
    justifyContent:'center'
  },
  paper:{
    marginLeft: 18,
    marginRight: 18
  },
  table:{
    marginTop:18,
    minWidth: 250,
  },
  grow: {
      flexGrow: 1,
    },
  tableHead: {
      fontSize: '1.0rem'
    },
    menuButton: {
      marginRight: theme.spacing(2),
      [theme.breakpoints.up('sm')]: {
        display: 'none',
      },
    },
  title: {
      display: 'none',
      [theme.breakpoints.up('xs')]: {
      display: 'block',
      },
    },
    drawer:{
    display: 'none',
    width:'35%',
    [theme.breakpoints.down('sm')]:{
      width: "80%",
    }
  },
    toolbar: theme.mixins.toolbar,
      drawerPaper: {
        width: drawerWidth,
      },
    content: {
      flexGrow: 1,
      padding: theme.spacing(3),
   },
}));

function App(props){
  const { container } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [drawerOpen, setDrawerOpen] = React.useState(false);
  const [users, setUsers] = React.useState('');

  const handleDrawerToggle = () =>{
    setDrawerOpen(!drawerOpen);
  }; 

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <Divider/>
      <List>
        <ListItem>

        </ListItem>
      </List>
      <List>
        {['Diary', 'To-Do', 'Calender'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemText primary={text}/>
          </ListItem>
        ))}
      </List>
    </div>
  );

  const stateRefresh= () =>{
      setUsers('');
      callApi().then((res) => {
        setUsers(res);
      }).catch((err)=>{console.log(err)});
    }



  const callApi = async () => {
    const response = await fetch('/api/users');
    const body = await response.json();
    return body;
  }
  const userCellList = ["아이디", "이메일", "이름"]

  return(

    <div className={classes.root}>
        <AppBar className={classes.appBar} position="static">
          <Toolbar>
            <IconButton  className={classes.menuButton} color="inherit" aria-label="Open drawer" onClick={handleDrawerToggle}>
              <MenuIcon/>
            </IconButton>
            <Typography className={classes.title} variant="h6" color="inherit" noWrap>
              MyToday
            </Typography>
            <div className={classes.menu}>
              <UserRegister stateRefresh={stateRefresh} />
            </div>
          </Toolbar>
        </AppBar>
        <nav className={classes.drawer} aria-label="mailbox folders">
          {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
          <Hidden smUp implementation="css">
            <Drawer
              container={container}
              variant="temporary"
              anchor={theme.direction === 'rtl' ? 'right' : 'left'}
              open={drawerOpen}
              onClose={handleDrawerToggle}
              classes={{
                paper: classes.drawerPaper,
              }}
              ModalProps={{
                keepMounted: true, // Better open performance on mobile.
              }}
            >
              {drawer}
            </Drawer>
          </Hidden>
          <Hidden xsDown implementation="css">
            <Drawer
              classes={{
                paper: classes.drawerPaper,
              }}
              variant="permanent"
              open
            >
              {drawer}
            </Drawer>
          </Hidden>
        </nav>
        <main className={classes.content}>
          <div className={classes.toolbar}>
            <Paper className={classes.paper}>
              <Table className={classes.table}> 
                <TableHead>
                    <TableRow>
                        {userCellList.map(u =>{
                          return <TableCell className={classes.tableHead}>{u}</TableCell>
                        })}
                    </TableRow>
                </TableHead>
                  <TableBody>
                    {users ? users.map(u => {
                      return <Users id={u.user_id}  email={u.user_email} name={u.user_name} />
                      }) : ''}
                  </TableBody>
                </Table>
            </Paper>
          </div>
        </main>

      </div>
  );
}

export default App;
