import React from 'react';
import './App.css';

//마테리얼 ui 컴포넌트들
import {Paper, Table, TableHead, TableBody, TableRow, TableCell, Typography, AppBar, Toolbar, IconButton, InputBase,} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu'
// import SearchIcon from '@material-ui/icons/Search'
import {withStyles} from '@material-ui/core/styles';
import { fade } from '@material-ui/core/styles/colorManipulator';


//제작한 리엑트 컴포넌트
import UserRegister from './components/UserRegister';
import Users from './components/Users';

const styles = theme =>({
  root:{
    width:'100%',
    marginTop: theme.spacing(3),
    overflowX:"auto"
  },
  menu:{

    marginTop: 15,
    marginBottom : 15,
    display: 'flax',
    justifyContent:'center'
  },
  paper:{
    marginLeft: 18,
    marginRight: 18
  },
  table:{
    minWidth: 500,
  },
  grow: {
      flexGrow: 1,
    },
  tableHead: {
      fontSize: '1.0rem'
    },
  menuButton: {
      marginLeft: -12,
      marginRight: 20,
    },
  title: {
      display: 'none',
      [theme.breakpoints.up('sm')]: {
      display: 'block',
      },
    },
  search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
        },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing.unit,
        width: 'auto',
      },
    },
  searchIcon: {
      width: theme.spacing.unit * 9,
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
  inputRoot: {
    color: 'inherit',
    width: '100%',
    },
  inputInput: {
      paddingTop: theme.spacing.unit,
      paddingRight: theme.spacing.unit,
      paddingBottom: theme.spacing.unit,
      paddingLeft: theme.spacing.unit * 10,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        width: 120,
      '&:focus': {
        width: 200,
        },
      },
    }
});


class App extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      users:'',
      mode:'diary',
    }   
    this.stateRefresh = this.stateRefresh.bind(this); 
  }


  stateRefresh(){
    this.setState({
       users:''
    })
    this.callApi().then((res) => {
      this.setState({
        users: res
      })
    }).catch((err)=>{console.log(err)});
  }

  componentDidMount(){
    this.callApi().then(res => this.setState({users: res})).catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/users');
    const body = await response.json();
    return body;
  }


  render(){
    const {classes} = this.props;
    const userCellList = ["아이디", "이메일", "이름"]
    return(
      <div className={classes.root}>
        <AppBar position="static">
          <Toolbar>
            <IconButton  className={classes.menuButton} color="inherit" aria-label="Open drawer">
              <MenuIcon/>
            </IconButton>
            <Typography className={classes.title} variant="h6" color="inherit" noWrap>
              MyToday
            </Typography>
            <div className={classes.menu}>
              <UserRegister stateRefresh={this.stateRefresh} />
            </div>
          </Toolbar>
        </AppBar>

        <Paper className={classes.paper}>
          <Table className={classes.table}>
                <TableHead>
                    <TableRow>
                        {userCellList.map(u =>{
                          return <TableCell className={classes.tableHead}>{u}</TableCell>
                        })}
                    </TableRow>
                </TableHead>
                  <TableBody>
                    {this.state.users ? this.state.users.map(u => {
                      return <Users id={u.user_id}  email={u.user_email} name={u.user_name} />
                      }) : ''}
                  </TableBody>
              </Table>
          </Paper>
      </div>
    )

  }
}


export default withStyles(styles)(App);
