import React from 'react';
import './App.css';

//마테리얼 ui 컴포넌트들
import {Paper, Table, TableHead, TableBody, TableRow, TableCell, Typography, AppBar, Toolbar, IconButton, InputBase,} from '@material-ui/core';
import {Menu, Search} from '@material-ui/icons'
import {withStyles} from '@material-ui/core/styles';


//제작한 리엑트 컴포넌트
import UserRegister from './components/UserRegister';
import Users from './components/Users';

const styles = theme =>({
  root:{
    width:'100%',
    marginTop: theme.spacing(3),
    overflowX:"auto"
  },
  menu:{
    marginTop: 15,
    marginBottom : 15,
    display: 'flax',
    justifyContent:'center'
  },
  paper:{
    marginLeft: 18,
    marginRight: 18
  },
  table:{
    minWidth: 500,
  },
  grow: {
      flexGrow: 1,
    },
  tableHead: {
      fontSize: '1.0rem'
    },
  menuButton: {
      marginLeft: -12,
      marginRight: 20,
    },
  title: {
      display: 'none',
      [theme.breakpoints.up('sm')]: {
      display: 'block',
      },
    },
  search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
        },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing.unit,
        width: 'auto',
      },
    },
  searchIcon: {
      width: theme.spacing.unit * 9,
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
  inputRoot: {
    color: 'inherit',
    width: '100%',
    },
  inputInput: {
      paddingTop: theme.spacing.unit,
      paddingRight: theme.spacing.unit,
      paddingBottom: theme.spacing.unit,
      paddingLeft: theme.spacing.unit * 10,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        width: 120,
      '&:focus': {
        width: 200,
      },
    },
    }
});


class App extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      users:'',
    }   
    this.stateRefresh = this.stateRefresh.bind(this); 
  }


  stateRefresh(){
    this.setState({
       users:''
    })
    this.callApi().then((res) => {
      this.setState({
        users: res
      })
    }).catch((err)=>{console.log(err)});
  }

  componentDidMount(){
    this.callApi().then(res => this.setState({users: res})).catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/users');
    const body = await response.json();
    return body;
  }


  render(){
    const {classes} = this.props;
    return(
      <div>
        <Paper className={classes.root}>
          <Table className={classes.table}>
                <TableHead>
                    <TableRow>
                        <TableCell>아이디</TableCell>
                        <TableCell>비밀번호</TableCell>
                        <TableCell>이메일</TableCell>
                        <TableCell>이름</TableCell>
                    </TableRow>
                </TableHead>
                  <TableBody>
                    {this.state.users ? this.state.users.map(u => {
                      return <Users id={u.user_id} pass={u.user_pass} email={u.user_email} name={u.user_name} />
                      }) : ''}
                  </TableBody>
              </Table>
          </Paper>
        <UserRegister stateRefresh={this.stateRefresh} />
      </div>
    )

  }
}


export default withStyles(styles)(App);
