import React, {Component} from 'react';
import {post} from 'axios'


class Login extends Component{
    constructor(props){
        super(props);
        this.state ={
            id:'',
            pass:'',
            pass_same:false,
            open:false,
        }
        this.handleFormSubmit = this.handleFormSubmit.bind(this)
        this.handleValueChange = this.handleValueChange.bind(this)
        this.loginProcess = this.loginProcess.bind(this)
        this.handleClickOpen = this.handleClickOpen.bind(this)
        this.handleClose = this.handleClose.bind(this)
    }

    handleFormSubmit(e){
        e.preventDefault()
        this.addUser().then((response)=>{
            this.props.stateRefresh();
        })
        this.setState({
            id:'',
            pass:'',
            pass_same:false,
            open:false,
        })
    }

    handleValueChange(e) {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
    }

    loginProcess(){
        if(this.state.pass !== this.state.pass_confirm){
            alert('비밀번호를 똑같이 입력해주세요');
            this.setState({pass_same : false});
        }else{
            this.setState({pass_same : true});
        }
        if(this.state.pass_same){

        }
        return post('api/users', {
            id: this.state.id,
            password: this.state.pass,
            email : this.state.email,
            name : this.state.name
        }).then((response) =>{
            console.log(response);
        })
    }

    render(){
        const{classes} = this.props;
        return(
            <div className='Login'>
                {/* 폼으로 구현시 */}
                <form onSubmit={this.handleFormSubmit}>
                    <h1>로그인</h1>
                    <input type="text" name='id' placeholder='id' value={this.state.id} onChange={this.handleValueChange}></input><br></br>
                    <input type="password" name='pass' placeholder='password' value={this.state.pass} onChange={this.handleValueChange}></input> <br></br>
                    <button type="submit">Login</button>
                </form>
            </div>
        )
    }
}

export default Login
