        }
        return post('api/users', {
            id: this.state.id,
            password: this.state.pass,
            email : this.state.email,
            name : this.state.name
        }).then((response) =>{
            console.log(response);
        })
    }

    render(){
        const{classes} = this.props;
        return(
            <div className='RegisterForm'>
                <Button variant="contained" color="primary" onClick={this.handleClickOpen}>
                    회원가입
                </Button>
                <Dialog className={st} open={this.state.open} onClose={this.handleClose}>
                    <DialogTitle>
                        회원가입
                    </DialogTitle>
                    <DialogContent>
                        <TextField label="아이디" type="text" name="id" value={this.state.id} onChange={this.handleValueChange} /> <br/><br/>
                        <TextField label="비밀번호" type="password" name="pass" value={this.state.pass} onChange={this.handleValueChange} /> <br/><br/>
                        <TextField label="비밀번호 재입력" type="password" name="pass_confirm" value={this.state.pass_confirm} onChange={this.handleValueChange} /> <br/><br/>
                        <TextField label="이메일" type="email" name="email" value={this.state.email} onChange={this.handleValueChange} /> <br/><br/>
                        <TextField label="이름" type="text" name="name" value={this.state.name} onChange={this.handleValueChange} /> <br/><br/>
                    </DialogContent>
                    <DialogActions>
                        <Button variant="contained" color="primary" onClick={this.handleFormSubmit}>추가</Button>
                        <Button variant="outlined" color="primary" onClick={this.handleClose}>닫기</Button>
                    </DialogActions>
                </Dialog>
                {/* 폼으로 구현시 */}
                {/* <form onSubmit={this.handleFormSubmit}>
                    <h1>회원가입</h1>
