        this.setState({
            id:'',
            pass:'',
            pass_confirm:'',
            email:'',
            name:'',
            pass_same:false,
            open:false,
        })
    }

    addUser(){
        if(this.state.pass !== this.state.pass_confirm){
            alert('비밀번호를 똑같이 입력해주세요');
